import React from "react";
import '.App.css';
import { Axios } from "axios";
import { useState, useEffect } from "react";

const App = () => {

  const [ search, setSearch ] = useState('');
  const [ crypto, setCrypto ] = useState([]);

  // fetching crypto data from api

  useEffect(() => {
    Axios.get(`https://api.coinstats.app/public/v1/coins?skip=0&limit=100Â¤cy=INR`).then
    ((res) => {
      setCrypto(res.data.coins);
    });
  }, []);


  return (
    <div className="App">
      <h1>Crypto Currency Latest</h1>
      <input
        type="text"
        placeholder="Search..."
        onChange={(e) => {
          setSearch(e.target.value);
        }}
      />
      <table>
        <thead>
          <tr>
            <td>Rank</td>
            <td>Name</td>
            <td>Symbol</td>
            <td>Market Cap</td>
            <td>Price</td>
            <td>Available Supply</td>
            <td>Volume(24hrs)</td>
          </tr>
        </thead>
        {/* {map all crypto} */}
        <tbody>
          {crypto.filter((val) => {
            return val.name.toLowerCase().includes(search.toLowerCase());
          })
          .map((val, id) => {
            return (
              <>
                <tr id={id}>
                  <td className="rank">{val.rank}</td>
                  <td className="logo">
                    <a href={val.websiteUrl}>
                      <img src={val.icon} alt='logo' width='30px' />
                    </a>
<p>{val.name}</p>
                  </td>
                  <td className="symbol">{val.symbol}</td>
                  <td>â¹{val.marketCap}</td>
                    <td>â¹{val.price.toFixed(2)}</td>
                    <td>{val.availableSupply}</td>
                    <td>{val.volume.toFixed(0)}</td>
                </tr>

              </>
            )
          })}
        </tbody>
      </table>


    </div>
  );
}

export default App;
