import axios, { Axios } from "axios";
import { useState, useEffect } from "react";

const App = () => {

  const [ search, setSearch ] = useState('');
  const [ crypto, setCrypto ] = useState([]);

  // fetching crypto data from api

  useEffect(() => {
    Axios.get(`https://api.coinstats.app/public/v1/coins?skip=0&limit=100¤cy=INR`).then
    ((res) => {
      setCrypto(res.data.coin)
    });
  }, []);


  return (
    <div>Crypto App</div>
  )
}

export default App;
